Number.prototype["+"] = function(...args) {
  try {
    let sum = this;
    for (let i = 0; i < args.length; i++) {
      sum += args[i];
    }
    return sum;
  } catch (error) {
    throw new Error("Error en +\n", error);
  }
}

// Ejemplo sencillo para entender el concepto
String.prototype["monke"] = function(...args) {
  try {
    return "This is monkey-patching";
  } catch (error) {
    throw new Error("Error en monke\n", error);
  }
}

Object.prototype["sub"] = function(...args) {
  let tmp = this;
  for (let i = 0; i < args.length; i++) {
    if (args[i] < 0) {
      tmp = tmp[tmp.length + args[i]];
    } else {
      if (tmp instanceof Map) tmp = tmp.get(args[i]);
      else tmp = tmp[args[i]];
    }
  }
  return tmp;
};